arguments.argument:
with(owner) {
    if (ability[0] != ALARM) abilityActive[0] = false;
    if (ability[0] == INVULN)
    {
        currentWeapon.readyToShoot = true;
        currentWeapon.readyToStab = true;
        currentWeapon.readyToFlare = true;
        currentWeapon.readyToBlast = true;
        if (weapons[0] == BonkHand and activeWeapon == 0)
        {
            playsound(x,y,UberEndSnd);
            owner._oldTauntSprite = owner.tauntsprite;
            tauntsprite = ScoutBonkS;
            tauntlength = sprite_get_number(tauntsprite)/2;
            tauntindex=tauntlength * player.team;
            tauntend=tauntlength * (1 + player.team) - 1;
            tauntspeed = 4;
        }
    }
    if (rechargeAbility[0] == ALARM)
    {
        meter[0] = maxMeter[0];
        if (weaponType[0] == THROWABLE and activeWeapon == 0) currentWeapon.ammoCount = currentWeapon.maxAmmo;
    }
    if (weaponType[0] != CONSUMABLE) playsound(x,y,rechargeSnd);
}
