arguments.argument:
with(owner) {
    if (cancelAbility[1] == ACANCEL_ALARM) abilityActive[1] = false;
    switch(ability[1])
    {
        case ABILITY_INVULN:
            currentWeapon.readyToShoot = true;
            currentWeapon.readyToStab = true;
            currentWeapon.readyToFlare = true;
            currentWeapon.readyToBlast = true;
            playsound(x,y,UberEndSnd);
            maxDuration  = 121;
        break;
        case ABILITY_CRIT:
        case ABILITY_MINICRIT:
            currentWeapon.crit = 1;
        break;
    }
    if (rechargeAbility[1] == ACHARGE_ALARM)
    {
        meter[1] = maxMeter[1];
        if (weaponType[1] == WTYPE_THROWABLE and currentWeapon.activeWeapon == 1) currentWeapon.ammoCount = currentWeapon.maxAmmo;
    } 
    if (depleteAbility[1] == ADEPLETE_ALARM) meter[1] = 0;
    if (weaponType[1] != WTYPE_CONSUMABLE) playsound(x,y,rechargeSnd);
}
