arguments.argument:
with(owner) {
    abilityActive[1] = false;
    if (ability[1] == INVINC)
    {
        currentWeapon.readyToShoot = true;
        currentWeapon.readyToStab = true;
        currentWeapon.readyToFlare = true;
        currentWeapon.readyToBlast = true;
    }
    if (currentWeapon.object_index == BonkHand)
    {
        if (owner.team == TEAM_BLUE) owner._oldTauntSprite = ScoutBlueTauntS; else owner._oldTauntSprite = ScoutRedTauntS;
        owner.tauntsprite = ScoutBonkS;
        owner.tauntlength = sprite_get_number(owner.tauntsprite)/2;
        owner.tauntindex=owner.tauntlength * owner.player.team;
        owner.tauntend=owner.tauntlength * (1 + owner.player.team) - 1;
        owner.tauntspeed = 4;
    }
    if (rechargeAbility[1] == ALARM)
    {
        meter[1] = maxMeter[1];
    }
    playsound(x,y,PickupSnd);
}
