arguments.argument:
with(owner) {
    if (ability[1] != ALARM) abilityActive[1] = false;
    if (ability[1] == INVINC)
    {
        currentWeapon.readyToShoot = true;
        currentWeapon.readyToStab = true;
        currentWeapon.readyToFlare = true;
        currentWeapon.readyToBlast = true;
        // todo: call for weapons[0] being BonkHand, need to work on throwable timer atm
        if (weapons[1] == BonkHand and activeWeapon == 1)
        {
            playsound(x,y,UberEndSnd);
            if (team == TEAM_BLUE) _oldTauntSprite = ScoutBlueTauntS; else _oldTauntSprite = ScoutRedTauntS;
            tauntsprite = ScoutBonkS;
            tauntlength = sprite_get_number(tauntsprite)/2;
            tauntindex=tauntlength * player.team;
            tauntend=tauntlength * (1 + player.team) - 1;
            tauntspeed = 4;
        }
    }
    if (rechargeAbility[1] == ALARM)
    {
        meter[1] = maxMeter[1];
        if (weaponType[1] == THROWABLE and activeWeapon == 1) currentWeapon.ammoCount = currentWeapon.maxAmmo;
    }
    playsound(x,y,rechargeSnd);
}
