arguments.argument:
if(other.id != ownerPlayer.object and other.team != team  and other.hp > 0 and !other.ubered)
{
    var connecting, otherId;
    connecting = false;
    otherId = other.id;
    if(ownerPlayer.object != -1)
    {
        with(ownerPlayer.object)
            connecting |= place_meeting(x, y, otherId);
    }
    connecting |= !collision_line_bulletblocking(x, y-12, other.x, other.y);
    if(connecting)
    {
        projectileCollision(ownerPlayer, other, hitDamage, object_index, 70);
        // todo: move bottom (and other such areas such as gibbing by Rocket) to projectileCollision via projectile object_index check
        if sign(other.image_xscale) == sign(image_xscale)
            other.lastDamageSource = DAMAGE_SOURCE_BACKSTAB;
        else
            other.lastDamageSource = DAMAGE_SOURCE_KNIFE;

        numKills += 1;
        if (numKills >= maxKills)
            instance_destroy();
    }
    else
        instance_destroy();
}
