arguments.argument:
if other.team=team && other.hp<other.maxHp && other.player != ownerPlayer {
    //other.hp += other.maxHp
    other.burnIntensity = 0;
    other.burnDuration = 0;
    other.hp += 45;
    if other.hp > other.maxHp other.hp = other.maxHp;
    with(Text)
    {
        if (variable_local_exists("owner")) {
            if (owner == other.owner) {
                instance_destroy(); // cleaner, and closer to original tf2
            }    
        }
    }
    var text;
    text=instance_create(other.x,other.y,Text);
    text.sprite_index=Plus45S;
    if (instance_exists(owner)) text.owner = owner;
    instance_destroy();
}
