arguments.argument:
if charge {
    justShot = true;
    time+=1;
    if time >= 89 {
        var oid;
        ammoCount -= 1;

        oid = createShot(x+lengthdir_x(10,owner.aimDirection),y+lengthdir_y(10,owner.aimDirection), Grenade, DAMAGE_SOURCE_GRENADE, owner.aimDirection, 17);
        oid.image_angle = 0;
        oid.vspeed-=2.5;
        oid.alarm[2] = 90-min(89,time);
        oid.explodeOnChar = false;
        oid.explosionDamage = 60;
        oid.slowOnCollide = false;
        readyToShoot = false;
        alarm[0] = refireTime / global.delta_factor;
        alarm[5] = (reloadBuffer + reloadTime) / global.delta_factor;

        charge = false;
        time = 0;
        alarm[3] = -1;
    }
}
