arguments.argument:
{
    xoffset = -8;
    yoffset = -2;
    refireTime = 28;

    reloadTime = 15;
    reloadBuffer = 26;
    maxAmmo = 4;
    event_inherited();

    ammoCount = owner.ammo[owner.activeWeapon];
    idle=true;

    damSource = DAMAGE_SOURCE_GRENADE;

    if (ammoCount < maxAmmo) alarm[5] = (reloadBuffer + reloadTime) / global.delta_factor;

    normalSprite = grenadeLauncherS;
    recoilSprite = grenadeLauncherFS;
    reloadSprite = grenadeLauncherS;

    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite) / 2;
    recoilImageSpeed = recoilAnimLength / recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite) / 2;
    reloadImageSpeed = reloadAnimLength / reloadTime;
}
