arguments.argument:
{
    xoffset = -7;
    yoffset = -3;
    refireTime = 3;

    maxAmmo = 40;
    reloadTime = 55;
    reloadBuffer = 1;
    event_inherited();

    ammoCount = owner.ammo[activeWeapon];

    idle=true;

    weaponGrade = WGRADE_STOCK;
    weaponType = WTYPE_SHOT;

    normalSprite = TerminalBreathS;
    recoilSprite = TerminalBreathFS;
    reloadSprite = TerminalBreathS;

    sprite_index = normalSprite;

    //This makes it so that the timer will keep resetting
    //Until Mouse1 is let go
    recoilTime = refireTime+1;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
