arguments.argument:
if (owner.taunting && (owner.tauntsprite == MedicRedTKillS || owner.tauntsprite == MedicBlueTKillS)) {
    if (owner.tauntindex >= TKF && !TK) {
        shot = instance_create(x,y,MeleeMask);
        shot.direction = owner.aimDirection;
        shot.speed = owner.speed;
        shot.owner = owner;
        shot.ownerPlayer = ownerPlayer;
        shot.team = owner.team;
        shot.hitDamage = 200;
        shot.weapon=DAMAGE_SOURCE_UBERSAW;
        playsound(x,y,BallSnd);
        TK = true;
    }
} 
else 
{
    TK = false;
}
