    chargeTime = 105;
    hitDamage=baseDamage;
    ammoCount = maxAmmo;
    shot=false;
    t=0;
    crit = 1;
    damSource = DAMAGE_SOURCE_RIFLE;
    weapon = damSource;
    revSlowdown = 0.6;
    jumpSlowdown = 6+(0.6/2);

    if(golden)
    {
        normalSprite = RifleGoldS;
        recoilSprite = RifleGoldFS;
        reloadSprite = RifleGoldFRS;
    }
    else
    {
        normalSprite = RifleS;
        recoilSprite = RifleFS;
        reloadSprite = RifleFRS;
    }
