    event_inherited();

    ammoCount = maxAmmo;
    idle=true;

    isMelee = true;

    ability = ABILITY_INVULN;
    abilityName = "BONK";
    abilityVisual = " BLUR ";
    rechargeAbility = ACHARGE_ALARM;
    cancelAbility = ACANCEL_ALARM;
    //activateAbility = AACTIVATE_TAUNT;
    //abilityBuffer = ABUFFER_ACTIVE;

