arguments.argument:
{
    xoffset= -3;
    yoffset= -2;
    refireTime = 18;

    maxAmmo = 1;

    reloadTime = 300;
    reloadBuffer = refireTime;
    event_inherited();

    ammoCount = maxAmmo;
    idle=true;

    isMelee = true;

    // used to be INVIS, maybe make that later?
    ability = INVULN;
    abilityName = "BONK";
    abilityVisual = "BLUR";
    rechargeAbility = ALARM;
    cancelAbility = ALARM;

    owner.eatCooldown = 300;
    maxMeter = owner.eatCooldown;
    meter = maxMeter;

    weaponGrade = UNIQUE;
    weaponType = CONSUMABLE;

    normalSprite = BonkHandS;
    recoilSprite = BonkHandS;
    reloadSprite = BonkHandS;

    sprite_index = normalSprite;

    image_speed = 0;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite) / 2;
    recoilImageSpeed = recoilAnimLength / recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite) / 2;
    reloadImageSpeed = reloadAnimLength / reloadTime;

    if (!owner.abilityActive[owner.activeWeapon]) {
        owner._oldTauntSprite = owner.tauntsprite;
        owner.tauntsprite = ScoutBonkS;
        owner.tauntlength = sprite_get_number(owner.tauntsprite)/2;
        owner.tauntindex=owner.tauntlength * owner.player.team;
        owner.tauntend=owner.tauntlength * (1 + owner.player.team) - 1;
        owner.tauntspeed = 4;
    }
}
