arguments.argument:
{
    xoffset = -5;
    yoffset = -2;
    refireTime = 20;

    maxAmmo = 6;
    reloadTime = 15;
    reloadBuffer = 20;
    event_inherited();

    ammoCount = owner.ammo[owner.activeWeapon];;

    idle=true;

    if(golden)
    {
        normalSprite = ShotgunGoldS;
        recoilSprite = ShotgunGoldFS;
        reloadSprite = ShotgunGoldFRS;
    }
    else
    {
        if (ownerPlayer.class == CLASS_PYRO) {
            normalSprite = PyroShotgunS;
            recoilSprite = PyroShotgunFS;
            reloadSprite = PyroShotgunFRS;
        /*else if (ownerPlayer.class == CLASS_HEAVY) {
            normalSprite = HeavyShotgunS;
            recoilSprite = HeavyShotgunFS;
            reloadSprite = HeavyShotgunFRS;
        */
        } else if (ownerPlayer.class == CLASS_SOLDIER) {
            normalSprite = SoldierShotgunS;
            recoilSprite = SoldierShotgunFS;
            reloadSprite = SoldierShotgunFRS;
        } else {
            normalSprite = ShotgunS;
            recoilSprite = ShotgunFS;
            reloadSprite = ShotgunFRS;
        }
    }
    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
