arguments.argument:
{
    refireTime = 1;

    maxAmmo = 200;
    reloadTime = 1;
    reloadBuffer = 7;
    event_inherited();

    blastDistance = 150;
    blastAngle = 75;
    blastStrength = 28;
    characterBlastStrength = 15;
    blastNoFlameTime = 15;
    blastReloadTime = 40;
    flareReloadTime = 55;

    alarm[1] = blastReloadTime / global.delta_factor;
    alarm[2] = flareReloadTime / global.delta_factor;
    readyToBlast = false;
    readyToFlare = false;
    justBlast = false;

    ammoCount = owner.ammo[activeWeapon];
    soundLoopTime = 30;
    currentTime = 0;

    flameDrain = 1.8;
    flameDamage = 3.14;
    blastDrain = 40;

    canBlast = true;
    damageOnBlast = false;
    airblastMove = 3;
    airblastDamage = 15;

    specialFlame = Flame;

    damSource = DAMAGE_SOURCE_FLAMETHROWER;
    weapon = damSource;

    reloadBuffer = 7;
    isRefilling = false;

    weaponGrade = WGRADE_UNIQUE;
    weaponType = WTYPE_FLAMETHROWER;

    if (!variable_local_exists("normalSprite")) normalSprite = FlamethrowerS;
    if (!variable_local_exists("recoilSprite")) recoilSprite = FlamethrowerFS;
    if (!variable_local_exists("blastSprite")) blastSprite = FlamethrowerBlastS;
    if (!variable_local_exists("dropSprite")) dropSprite = FlamethrowerDropS;
    reloadSprite = -1;

    sprite_index = normalSprite;

    //play at least 2 frames
    recoilTime = 3;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    blastAnimLength = sprite_get_number(blastSprite)/2;
    blastImageSpeed = blastAnimLength/blastNoFlameTime;

    dropTime = 4;
    dropAnimLength = sprite_get_number(dropSprite)/2;
    dropImageSpeed = dropAnimLength/dropTime;
}
