shot = instance_create(x,y,MeleeMask);
shot.hitDamage = damage;
shot.direction = owner.aimDirection;
shot.speed = owner.speed;
shot.owner = owner;
shot.ownerPlayer = ownerPlayer;
shot.team = owner.team;
shot.hitDamage = damage;
shot.weapon = damSource;
shot.crit = owner.currentWeapon.crit;

for(i=0; i<2; i+=1) {
    // todo: move this clump to Character Begin Step, maybe?
    if (owner.abilityActive[i] && owner.ability[i] == ABILITY_DASH) {
        shot.splashHit = true;
        shot.splashAmount = 3;
        shot.knockBack = true;
        if (owner.meter[i] <= 50 || owner.moveStatus == 4 && owner.meter[i] <= 56) { // give grace since youre flying
            playsound(x,y,CritSnd);
        } else if (owner.meter[i] <= 70 || owner.moveStatus == 4 && owner.meter[i] <= 76) {
            //shotDamage = 40; 
            playsound(x,y,CritSnd);
        }
        owner.meter[i] = 0;
        owner.abilityActive[i] = false;
        crit = 1;
    }
}
