arguments.argument:
{
    refireTime = 18;
    event_inherited();

    SwingreloadTime = 5;
    StabreloadTime = 32;
    if (alarm[2] <= 0) alarm[2] = 5;
    smashing = false;
    isMelee = true;
    readyToStab = true;

    stabdirection=0;
    maxAmmo = 1;
    ammoCount = maxAmmo;
    reloadTime = 300;
    reloadBuffer = refireTime;
    idle=true;

    damSource = DAMAGE_SOURCE_KNIFE;

    damage = 10;

    if (!variable_local_exists("spriteBase")) spriteBase = Knife2S;
    if (!variable_local_exists("spriteRecoil")) spriteRecoil = Knife2FS;
    if (!variable_local_exists("spriteReload")) spriteReload = Knife2S;

    normalSprite = spriteBase;
    recoilSprite = spriteRecoil;
    reloadSprite = spriteReload;

    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
