arguments.argument:
{
    refireTime = 18;

    reloadTime = 300;
    reloadBuffer = refireTime;
    maxAmmo = 1;
    event_inherited();

    SwingreloadTime = 5;
    StabreloadTime = 32;
    if (alarm[2] <= 0) alarm[2] = 5;
    smashing = false;
    isMelee = true;
    readyToStab = true;

    stabdirection=0;

    ammoCount = maxAmmo;

    idle=true;

    damSource = DAMAGE_SOURCE_KNIFE;

    damage = 35;

    weaponGrade = WGRADE_UNIQUE;
    weaponType = WTYPE_MELEE;

    if (!variable_local_exists("normalSprite")) normalSprite = Knife2S;
    if (!variable_local_exists("recoilSprite")) recoilSprite = Knife2FS;
    if (!variable_local_exists("reloadSprite")) reloadSprite = Knife2S;

    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
