arguments.argument:
{
    /* template 
    xoffset = -3;
    yoffset = -2;
    refireTime = 26;

    maxAmmo = 8;
    reloadTime = 15;
    reloadBuffer = 26;
    maxMines = 8;

    normalSprite = MinegunS;
    recoilSprite = MinegunFS;
    reloadSprite = MinegunFRS;*/
    event_inherited();

    // default vars
    ammoCount = owner.ammo[owner.activeWeapon];
    idle=true;

    weaponGrade = UNIQUE;
    weaponType = MINEGUN;

    damSource = DAMAGE_SOURCE_MINEGUN;

    specialProjectile = Mine;
    projectileSpeed = 12;
    projectilePosition = 10;
    projectileBlastRadius = 40;

    specialSnd = MinegunSnd;

    fullReload = false;

    splashDamage = 45;
    ammoDrain = 1;
    ammoGain = 1;

    destructProjectilesOnDeath = true;
    canDetonate = true; // implemented for StickySticker

    if (!variable_local_exists("normalSprite")) normalSprite = MinegunS;
    if (!variable_local_exists("recoilSprite")) recoilSprite = MinegunFS;
    if (!variable_local_exists("reloadSprite")) reloadSprite = MinegunFRS;

    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
