arguments.argument:
{
    event_inherited();

    // default vars
    ammoCount = owner.ammo[owner.activeWeapon];
    idle=true;

    weaponGrade = UNIQUE;
    weaponType = SHOT;

    damSource = DAMAGE_SOURCE_ROCKETLAUNCHER;

    specialProjectile = Rocket;
    projectileSpeed = 13;
    projectilePosition = 20;

    specialSpriteR = RedRocketS;
    specialSpriteB = BlueRocketS;
    specialSnd = RocketSnd;

    shotDamage = 7;
    fullReload = false;

    directDamage = 25;
    splashDamage = 30; 

    if (!variable_local_exists("normalSprite")) normalSprite = RocketlauncherS;
    if (!variable_local_exists("recoilSprite")) recoilSprite = RocketlauncherFS;
    if (!variable_local_exists("reloadSprite")) reloadSprite = RocketlauncherFRS;

    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
