arguments.argument:
ammoCount = max(0, ammoCount-1);
playsound(x,y,specialSnd);
var oid, newx, newy;
newx = x+lengthdir_x(projectilePosition,owner.aimDirection);
newy = y+lengthdir_y(projectilePosition,owner.aimDirection);
oid = createShot(newx, newy, specialProjectile, damSource, owner.aimDirection, projectileSpeed);
with (oid)
{
    if(variable_local_exists("hitDamage")) hitDamage = other.directDamage;
    if(variable_local_exists("explosionDamage")) explosionDamage = other.splashDamage;
    if (collision_line_bulletblocking(other.x, other.y, newx, newy))
    {
        // Delay explosion to make same-frame rocketjumping work reliably
        if (variable_local_exists("explodeImmediately")) explodeImmediately = true;
        else instance_destroy();
    }
}
oid.lastknownx = owner.x;
oid.lastknowny = owner.y;
justShot=true;
readyToShoot=false;
alarm[0] = refireTime / global.delta_factor;
alarm[5] = (reloadBuffer + reloadTime) / global.delta_factor;

if (crit > 1) playsound(x,y,CritSnd);
