arguments.argument:
{
    event_inherited();

    // default vars
    ammoCount = owner.ammo[owner.activeWeapon];
    idle=true;

    weaponGrade = UNIQUE;
    weaponType = SHOT;

    damSource = DAMAGE_SOURCE_SHOTGUN;

    specialShot = Shot;
    shots = 5;
    shotSpeed[0] = 13;
    shotSpeed[1] = 4;
    shotSpeed[2] = 2;
    shotDir[0] = 11;
    shotDir[1] = 5;
    shotPosition = 15; 

    /*chaingun template
        shotDir[0] = 14;
        shotDir[1] = 7;
        shotPosition = 20;
    */

    randomizeSpeed = true;

    specialSprite = ShotS;
    specialSnd = ShotgunSnd;

    shotDamage = 7;
    fullReload = false;

    reloadShell = false;

    // added for miniguns;

    chainGun = false;
    isRefilling = false;
    // special case for stuff like smgs, pistols
    refillChainGun = true;
    // exact values
    revSlowdown = 0.3;
    jumpSlowdown = owner.baseJumpStrength;

    if (!variable_local_exists("normalSprite")) normalSprite = ShotgunS;
    if (!variable_local_exists("recoilSprite")) recoilSprite = ShotgunFS;
    if (!variable_local_exists("reloadSprite")) reloadSprite = ShotgunFRS;

    sprite_index = normalSprite;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
