arguments.argument:
if(!chainGun and readyToShoot and ammoCount >0 and !owner.cloak and global.isHost)
{
    var seed;
    seed = irandom(65535);
    sendEventFireWeapon(ownerPlayer, seed);
    doEventFireWeapon(ownerPlayer, seed);
} else if (chainGun) {
    // prevent sputtering
    if (ammoCount < 2)
        ammoCount -= 2;

    if(readyToShoot and ammoCount >= 2 && !owner.cloak)
    {
        playsound(x,y,specialSnd);
        var shot, shotx, shoty;
        randomize();

        // todo: maybe setup shotPositionExtra

        shotx = x+lengthdir_x(shotPosition,owner.aimDirection);
        shoty = y+12+lengthdir_y(shotPosition,owner.aimDirection);
        if(!collision_line_bulletblocking(x, y, shotx, shoty))
        {
            shot = createShot(shotx, shoty, specialShot, damSource, owner.aimDirection+(random(shotDir[0])-shotDir[1]), shotSpeed[0]+random(shotSpeed[1]));
            shot.sprite_index = specialSprite;
            shot.hspeed += owner.hspeed;
            shot.alarm[0] = 30 / global.delta_factor;
            shot.hitDamage = shotDamage;
        }
        else
        {
            var imp;
            imp = instance_create(shotx, shoty, Impact);
            imp.image_angle = owner.aimDirection;
        }

        justShot=true;
        readyToShoot=false;
        isRefilling = false;
        ammoCount -= 3;

        var reloadBufferFactor;
        if(ammoCount < 3)
            reloadBufferFactor = 2.5;
        else
            //reloadBufferFactor = 1+(cos((ammoCount+2.2)/maxAmmo*pi)+1)/2; // spline from (full ammo = 1*) to (empty ammo = 2*)
            reloadBufferFactor = 1;

        alarm[0] = refireTime / global.delta_factor;
        alarm[5] = reloadBuffer*reloadBufferFactor / global.delta_factor;

        if (global.particles == PARTICLES_NORMAL)
        {
            var shell;
            shell = instance_create(x, y+4, Shell);
            shell.direction = owner.aimDirection + (140 - random(40)) * image_xscale;
        }
    }
}
