arguments.argument:
{
    refireTime = 18;

    maxAmmo = 1;

    reloadTime = 300;
    reloadBuffer = reloadTime;
    event_inherited();

    isMelee = true;

    ability = 50;
    rechargeAbility = ACHARGE_ALARM;
    maxMeter = 300;
    meter = maxMeter;

    if (owner.meter[activeWeapon] >= maxAmmo) ammoCount = maxAmmo; else ammoCount = 0;

    shotSpeed = 13;

    throwProjectile = -1;
    projectileSpeed = 13;
    randomDir = true;
    projectileDir[0] = 7;
    projectileDir[1] = 4;


    weaponType = WTYPE_THROWABLE;
    weaponGrade = WGRADE_UNIQUE;

    if (!variable_local_exists("normalSprite")) normalSprite = JarateHandS;
    if (!variable_local_exists("recoilSprite")) recoilSprite = JarateHandS;
    if (!variable_local_exists("reloadSprite")) reloadSprite = JarateHandS;

    sprite_index = normalSprite;

    image_speed = 0;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
