arguments.argument:
{
    refireTime = 18;
    event_inherited();

    isMelee = true;

    maxAmmo = 1;
    ammoCount = maxAmmo;

    reloadTime = 300;
    reloadBuffer = refireTime;

    shotSpeed = 13;

    throwProjectile = -1;
    projectileSpeed = 13;
    randomDir = true;
    projectileDir[0] = 7;
    projectileDir[1] = 4;

    ability = ALARM;
    rechargeAbility = ALARM;
    maxMeter = 1;
    meter = maxMeter;

    weaponType = THROWABLE;
    weaponGrade = UNIQUE;

    if (!variable_local_exists("spriteBase")) spriteBase = JarateHandS;
    if (!variable_local_exists("spriteRecoil")) spriteRecoil = JarateHandS;
    if (!variable_local_exists("spriteReload")) spriteReload = JarateHandS;

    normalSprite = spriteBase;
    recoilSprite = spriteRecoil;
    reloadSprite = spriteReload;

    sprite_index = normalSprite;

    image_speed = 0;

    recoilTime = refireTime;
    recoilAnimLength = sprite_get_number(recoilSprite)/2;
    recoilImageSpeed = recoilAnimLength/recoilTime;

    reloadAnimLength = sprite_get_number(reloadSprite)/2;
    reloadImageSpeed = reloadAnimLength/reloadTime;
}
