arguments.argument:
if(owner.meter[activeWeapon] == owner.maxMeter[activeWeapon] and readyToShoot and !owner.cloak) {
    playsound(x, y, swingSnd);
    owner.meter[activeWeapon] = 0;
    ammoCount = 0;
    shot = instance_create(x,y + yoffset + 1, throwProjectile);
    if (randomDir) shot.direction = owner.aimDirection + random(projectileDir[0]) - projectileDir[1]; else shot.direction = owner.aimDirection;
    shot.speed = projectileSpeed;
    shot.owner = owner;
    shot.ownerPlayer=ownerPlayer;
    shot.team = owner.team;
    with(shot)
        hspeed += owner.hspeed;    
    owner.tracker.alarm[activeWeapon] = owner.maxMeter[activeWeapon] / global.delta_factor;
    readyToShoot = false;
    alarm[0] = refireTime / global.delta_factor;
}
